import React from 'react';
import { Routes, Route, useNavigate } from "react-router-dom";
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import AddPlacePopup from './AddPlacePopup.js';
import Login from './Login.js';
import Register from './Register.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import { api } from '../utils/Api.js';
import ProtectedRouteElement from "./ProtectedRoute";


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);

  const [isEditProfilePopupOnLoading, setIsEditProfilePopupOnLoading] = React.useState(false);
  const [isAddPlacePopupOnLoading, setIsAddPlacePopupOnLoading] = React.useState(false);
  const [isEditAvatarPopupOnLoading, setIsEditAvatarPopupOnLoading] = React.useState(false);

  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);
  //auth
  const [loggedIn, setLoggedIn] = React.useState(false);
  const [userEmail, setUserEmail] = React.useState("");
  const navigate = useNavigate();
  React.useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
      .then(([userData, cards]) => {
        setCurrentUser(userData);
        setCards(cards);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const isOpen = isEditAvatarPopupOpen || isEditProfilePopupOpen || isAddPlacePopupOpen || selectedCard;

  React.useEffect(() => {
    function closeByEscape(evt) {
      if (evt.key === 'Escape') {
        closeAllPopups();
      }
    }
    if (isOpen) {
      document.addEventListener('keydown', closeByEscape);
      return () => {
        document.removeEventListener('keydown', closeByEscape);
      };
    }
  }, [isOpen]);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null);
  }
//авторизация,регистрация

function handleRegister({ password, email }) {
  // return register(password, email)
  //   .then(() => {
  //     setResponseInfo({
  //       status: true,
  //       text: "Вы успешно зарегистрировались!",
  //     });
  //     navigate("/sign-in", { replace: true });
  //   })
  //   .catch((err) => {
  //     setResponseInfo({
  //       status: false,
  //       text: `Что-то пошло не так! Попробуйте ещё раз. ${err}`,
  //     });
  //   })
  //   .finally(() => setOpenInfoTooltip(true));
}

function handleLogin({ password, email }) {
  // return login(password, email)
  //   .then((token) => {
  //     if (token) {
  //       localStorage.setItem("jwt", token.token);
  //       setLoggedIn(true);
  //       setUserEmail(email);
  //       navigate("/");
  //     }
  //   })
  //   .catch((err) => {
  //     if (err === "Ошибка: 401 Unauthorized") {
  //       setOpenInfoTooltip(true);
  //       setResponseInfo({
  //         status: false,
  //         text: "Такой аккаунт не зарегистрирован",
  //       });
  //     } else {
  //       setOpenInfoTooltip(true);
  //       setResponseInfo({
  //         status: false,
  //         text: `Что-то пошло не так! Попробуйте ещё раз. ${err}`,
  //       });
  //     }
  //   });
}

function handleSignOut() {
  // localStorage.removeItem("jwt");
  // navigate("/sign-in");
  // setLoggedIn(false);
  // setUserEmail("");
}



  //лайки карточек
  function handleCardLike(card) {
    const isLiked = card.likes.some((item) => item._id === currentUser._id);
    api
      .changeLikeCardStatus(card._id, isLiked)
      .then((newCard) => {
        setCards((state) => state.map((item) => (item._id === card._id ? newCard : item)));
      })
      .catch((err) => {
        console.log(err);
      });
  }
  //удаление карточки
  function handleCardDelete(card) {
    api
      .deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter((item) => item._id !== card._id));
      })
      .catch((err) => {
        console.log(err);
      });
  }
  //редактирование профиля
  function handleUpdateUser(inputs) {
    setIsEditProfilePopupOnLoading(true);
    api
      .setUserInfo(inputs)
      .then((data) => {
        setCurrentUser(data);
      })
      .then(() => {
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setIsEditProfilePopupOnLoading(false);
      });
  }
  //редактирование аватарки
  function handleUpdateAvatar(inputs) {
    setIsEditAvatarPopupOnLoading(true);
    api
      .setUserAvatar(inputs)
      .then((data) => {
        setCurrentUser(data);
      })
      .then(() => {
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setIsEditAvatarPopupOnLoading(false);
      });
  }
  //добавление карточки
  function handleAddPlaceSubmit(card) {
    setIsAddPlacePopupOnLoading(true);
    api
      .createCard(card)
      .then((data) => {
        setCards([data, ...cards]);
      })
      .then(() => {
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setIsAddPlacePopupOnLoading(false);
      });
  }
  return (
    <div className="page">
      <CurrentUserContext.Provider value={currentUser}>
      <Header loggedIn={loggedIn} userEmail={userEmail} onSignOut={handleSignOut} />
      <Routes>
      <Route
            path="/"
            element={
              <ProtectedRouteElement
                component={Main}
                loggedIn={loggedIn}
                cards={cards}
                onEditAvatar={handleEditAvatarClick}
                onEditProfile={handleEditProfileClick}
                onAddPlace={handleAddPlaceClick}
                onCardClick={handleCardClick}
                onCardLike={handleCardLike}
                onCardDelete={handleCardDelete}
              />
            }
          />
          <Route path="/sign-up" element={<Register onRegister={handleRegister} />} />
          <Route path="/sign-in" element={<Login onLogin={handleLogin} />} />
        </Routes> 
        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
          onLoading={isEditProfilePopupOnLoading}
        />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlace={handleAddPlaceSubmit}
          onLoading={isAddPlacePopupOnLoading}
        />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
          onLoading={isEditAvatarPopupOnLoading}
        />
        <PopupWithForm name="confirm" title="Вы&nbsp;уверены?" buttonText="Да" />
        <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
