import React from 'react';
import pen from '../images/botton_pen.svg';
import plus from '../images/botton_plus.svg';

function Main({ handleEditAvatarClick }) {
  this.handleEditAvatarClick = handleEditAvatarClick;

  function handleEditAvatarClick() {
    const element = document.querySelector('.profile__pen-avatar');
    element.addEventListener('click', () => {
      const elements = document.querySelector('.profile__pen-avatar');
      elements.classList.add('popup_opened');
    });
  }
  return (
    <main className="content page__content">
      <section className="profile content__section">
        <button className="profile__avatar-edit">
          <img src alt="Фото Жак-Ив Кусто" className="profile__avatar" />
          <img src={pen} alt="Ручка" className="profile__pen-avatar" onClick={handleEditAvatarClick} />
        </button>
        <div className="profile__info">
          <h1 className="profile__name">Жак-Ив Кусто</h1>
          <button type="button" className="profile__edit-button" aria-label="Кнопка редактировать">
            <img src={pen} alt="Ручка" className="profile__pen" />
          </button>
          <p className="profile__activities">Исследователь океана</p>
        </div>
        <button type="button" className="profile__add-button" aria-label="Кнопка добавления картинки">
          <img src={plus} alt="Плюс" className="profile__plus" />
        </button>
      </section>
      <section className="cards content__section" aria-label="Блок с карточками">
        <ul className="cards__list"></ul>
      </section>
    </main>
  );
}
export default Main;
