import React from 'react';
import { Link } from 'react-router-dom';
import PopupWithAuth from './PopupWithAuth.js';

function Register({ onRegister }) {
  const [formValue, setFormValue] = React.useState({
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormValue({
      ...formValue,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onRegister(formValue).then(() => {
      setFormValue({
        email: '',
        password: '',
      });
    });
  };

  return (
    <section className="auth">
      <div className="login">
        <h2 className="login__title">Регистрация</h2>
        <PopupWithAuth buttonText="Зарегистрироваться" onSubmit={handleSubmit}>
          <label className="popup__field">
            <input
              name="email"
              type="email"
              className="popup__input popup__input_type_login"
              placeholder="Email"
              required
              minLength="2"
              value={formValue.email}
              onChange={handleChange}
            />
            <span className="email-error"></span>
          </label>
          <label className="popup__field">
            <input
              name="password"
              type="password"
              className="popup__input popup__input_type_login"
              placeholder="Пароль"
              required
              minLength="6"
              value={formValue.password}
              onChange={handleChange}
              //   autoСomplete="current-password"
            />
            <span className="password-error"></span>
          </label>
        </PopupWithAuth>
        <p className="login__text">
          Уже зарегистрированы?
          <Link to="/sign-in" className="login__link">
            &nbsp;Войти
          </Link>
        </p>
      </div>
    </section>
  );
}

export default Register;
