import React from 'react';
import PopupWithAuth from './PopupWithAuth.js';

function Login({ onLogin }) {
  const [formValue, setFormValue] = React.useState({
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormValue({
      ...formValue,
      [name]: value,
    });
  };

  function handleSubmit(e) {
    e.preventDefault();
    onLogin(formValue).then(() => {
      setFormValue('');
    });
  }

  return (
    <section className="auth">
      <div className="login">
        <h2 className="login__title">Вход</h2>
        <PopupWithAuth name="login" title="Вход" buttonText="Войти" onSubmit={handleSubmit}>
          <label className="popup__field">
            <input
              name="email"
              id="email-login"
              type="email"
              className="popup__input popup__input_type_login"
              placeholder="Email"
              required
              minLength="2"
              value={formValue.email || ''}
              onChange={handleChange}
            />
            <span className="email-login-error"></span>
          </label>
          <label className="popup__field">
            <input
              name="password"
              type="password"
              id="password"
              className="popup__input popup__input_type_login"
              placeholder="Пароль"
              required
              minLength="6"
              value={formValue.password || ''}
              onChange={handleChange}
            />
            <span className="about-error"></span>
          </label>
        </PopupWithAuth>
      </div>
    </section>
  );
}

export default Login;
