import React from 'react';
import pen from '../images/botton_pen.svg';
import plus from '../images/botton_plus.svg';

function Main() {
  function handleEditAvatarClick() {
    const el = document.querySelector('.popup_add-image');
    el.classList.add('popup_opened');
    const elem = document.querySelector('.profile-popup');
    elem.classList.add('popup_opened');
    const element = document.querySelector('.popup-confirm');
    element.classList.add('popup_opened');
  }
  return (
    <main className="content page__content">
      <section className="profile content__section">
        <button className="profile__avatar-edit" onClick={handleEditAvatarClick}>
          <img src alt="Фото Жак-Ив Кусто" className="profile__avatar" />
          <img src={pen} alt="Ручка" className="profile__pen-avatar" />
        </button>
        <div className="profile__info">
          <h1 className="profile__name">Жак-Ив Кусто</h1>
          <button
            type="button"
            className="profile__edit-button"
            aria-label="Кнопка редактировать"
            onClick={handleEditAvatarClick}
          >
            <img src={pen} alt="Ручка" className="profile__pen" />
          </button>
          <p className="profile__activities">Исследователь океана</p>
        </div>
        <button
          type="button"
          className="profile__add-button"
          aria-label="Кнопка добавления картинки"
          onClick={handleEditAvatarClick}
        >
          <img src={plus} alt="Плюс" className="profile__plus" />
        </button>
      </section>
      <section className="cards content__section" aria-label="Блок с карточками">
        <ul className="cards__list"></ul>
      </section>
    </main>
  );
}
export default Main;
