import React from 'react';
import Success from '../images/Success.png';
import Fail from '../images/Fail.png';

function InfoTooltip(props) {
  return (
    <div className={`popup popup_type_tooltip ${props.isOpen ? 'popup_opened' : ' '}`}>
      <div className="popup__container popup__tooltip-container">
        <button
          type="button"
          className="popup__close"
          aria-label="Кнопка закрытия попапа"
          onClick={props.onClose}
        ></button>
        <div className="popup__content">
          <img className="popup__tooltip-image" src={props.isSuccess ? Success : Fail} alt="" />
          <p className="popup__tooltip-title">{props.isSuccess ? props.successMessage : props.FailMessage}</p>
        </div>
      </div>
    </div>
  );
}

export default InfoTooltip;
