import React from 'react';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import { api } from '../utils/Api.js';

function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);
  React.useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
      .then(([userData, cards]) => {
        setCurrentUser(userData);
        setCards(cards);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true);
  }
  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function closeAllPopups() {
    setEditAvatarPopupOpen(false);
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
    setSelectedCard(null);
  }
  function handleCardLike(card) {
    const isLiked = card.likes.some((item) => item._id === currentUser._id);
    api
      .changeLikeCardStatus(card._id, isLiked)
      .then((newCard) => {
        setCards((state) => state.map((item) => (item._id === card._id ? newCard : item)));
      })
      .catch((err) => {
        console.log(err);
      });
  }

  function handleCardDelete(card) {
    api
      .deleteCard(card._id)
      .then(() => {
        const newCards = cards.filter((c) => c._id !== card._id);
        setCards(newCards);
      })
      .catch((err) => {
        console.log(err);
      });

    // function handleCardDelete(card) {
    //   api.deleteCard(card._id)
    //   .then(() => {
    //     setCards((state) => state.filter((it) => item._id !== card._id));
    //     closeAllPopups();
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   })

    return (
      <div className="page">
        <CurrentUserContext.Provider value={currentUser}>
          <Header />
          <Main
            cards={cards}
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onCardClick={handleCardClick}
            onCardLike={handleCardLike}
            onCardDelete={handleCardDelete}
          />
          <Footer />
          <PopupWithForm
            name="profile"
            title="Редактировать профиль"
            buttonText="Сохранить"
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
          >
            <label className="popup__field">
              <input
                name="name"
                id="name-card"
                type="text"
                className="popup__input popup__input_type_name"
                placeholder="Введите имя"
                required
                minLength="2"
                maxLength="40"
              />
              <span className="name-card-error"></span>
            </label>
            <label className="popup__field">
              <input
                name="about"
                type="text"
                id="about"
                className="popup__input popup__input_type_aboutme"
                placeholder="Введите профессию"
                required
                minLength="2"
                maxLength="200"
              />
              <span className="about-error"></span>
            </label>
          </PopupWithForm>
          <PopupWithForm
            name="add-image"
            title="Новое место"
            buttonText="Создать"
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
          >
            <label className="popup__field">
              <input
                name="name"
                type="text"
                id="title-image"
                className="popup__input popup__input_type_title"
                placeholder="Название"
                required
                minLength="2"
                maxLength="30"
              />
              <span className="title-image-error"></span>
            </label>
            <label className="popup__field">
              <input
                name="link"
                type="url"
                id="link-image"
                className="popup__input popup__input_type_link"
                placeholder="Ссылка на картинку"
                required
              />
              <span className="link-image-error"></span>
            </label>
          </PopupWithForm>
          <PopupWithForm
            name="refresh"
            title="Обновить аватар"
            buttonText="Сохранить"
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
          >
            <label className="popup__field">
              <input
                name="avatar"
                id="link-avatar"
                type="url"
                className="popup__input popup__input_type_link-avatar"
                placeholder="Ссылка на картинку"
                required
              />
              <span className="link-avatar-error"></span>
            </label>
          </PopupWithForm>
          <PopupWithForm name="confirm" title="Вы&nbsp;уверены?" buttonText="Да" />
          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
        </CurrentUserContext.Provider>
      </div>
    );
  }
}
export default App;
